function formatB() {
	let n = Total_sad_count.data[0]["total_sad"]
	if (n > 999999999){
		return (n / 1000000000).toFixed(2) + "B";
	}
  else if(n > 999999){
    return (n/1000000).toFixed(2) + "M";
	}
	else if(n > 999){
    return (n/1000).toFixed(2) + "K";
	}
	else{
		return n;
	}
}()
}}

