SELECT Date_trunc('year', date)	AS timestamp, 
sum(cast(views as decimal)) as COUNT_Prevyear
FROM public."smg_dmp_youtube_dashboard_unq_view"
where date >= '{{moment().add(-1, 'year').format("YYYY-MM-01")}}':: date
  		AND date < '{{moment().add(-1, 'year').format("YYYY-12-31")}}':: date
			AND channel_display_name like {{Channel_select.selectedOptionValue ? Channel_select.selectedOptionValue : "%"}} 
	AND category like {{Category_select.selectedOptionValue ? Category_select.selectedOptionValue : "%"}}
	AND time_published > {{Video_type.selectedOptionValue}}
GROUP BY timestamp
order by timestamp desc
limit 1;
