SELECT SUM((select count(cast(total_video_views as decimal)) from public.smg_dmp_fb_video_dashboard_unq_view) + (select count(cast(views as decimal)) from public.smg_dmp_youtube_dashboard_unq_view )) as total_video_count
FROM public.smg_dmp_fb_video_dashboard_unq_view fb JOIN public.smg_dmp_youtube_dashboard_unq_view yt on fb.unq_id=yt.unq_id
limit 10;
