SELECT SUM((select sum(cast(total_video_view_total_time as decimal)/ 3600) from public.smg_dmp_fb_video_dashboard_unq_view) + (select sum(watch_time_hours) from public.smg_dmp_youtube_dashboard_unq_view )) as total_watch_count
FROM public.smg_dmp_fb_video_dashboard_unq_view fb JOIN public.smg_dmp_youtube_dashboard_unq_view yt on fb.unq_id=yt.unq_id
limit 10;
